export default {
	myVar1: [],
	myVar2: {},
	return_Current_card:()=>
	{
		if(appsmith.store.button_Number==0)
			{
				return appsmith.store.card[appsmith.store.current_Live_card]
			}
		else if(appsmith.store.button_Number==1)
			{
				return appsmith.store.card[appsmith.store.current_Rnr_card]
			}
		else if(appsmith.store.button_Number==2)
			{
				return appsmith.store.card[appsmith.store.current_Callback_card]
			}
		else if(appsmith.store.button_Number==3)
			{
				return appsmith.store.card[appsmith.store.current_Followups_card]
			}
		return appsmith.store.card[0]
	},
	return_current_Oldlead_card : ()=>{
	if(appsmith.store.button==1)
			{
				return appsmith.store.card[appsmith.store.current_Old_card]
			}
		return appsmith.store.card[0]
},
	return_current_Demo_card : ()=>{
	if(appsmith.store.button==2)
			{
				return appsmith.store.card[appsmith.store.current_Demo_card]
			}
		return appsmith.store.card[0]
},
	next_Demo_card : ()=>{
		if(appsmith.store.current_Demo_card>=appsmith.store.card.length-1)
						{
							GET_DEMO_LEADS.run();
							showAlert("Click DEMO button to Confirm","success")
							return appsmith.store.current_Old_card
						}
					storeValue("current_Demo_card",appsmith.store.current_Demo_card+1)
					return appsmith.store.current_Demo_card
	},
	next_Oldlead_card : ()=>{
		if(appsmith.store.current_Old_card>=appsmith.store.card.length-1)
						{
							GET_OLD_LEADS.run();
							showAlert("Click OLD LEADS button to Confirm","success")
							return appsmith.store.current_Old_card
						}
					storeValue("current_Old_card",appsmith.store.current_Old_card+1)
					return appsmith.store.current_Old_card
	},
	next_Card:()=>{
/*
		storeValue("current_lead_card",0) 
		storeValue("current_demo_card",0) 
		storeValue("current_call_back_card",0) 
		return
*/
		switch(appsmith.store.button_Number)
			{
				case 0:
					if(appsmith.store.current_Live_card>=appsmith.store.card.length-1)
						{
							GET_DATA.run();
							showAlert("Click LIVE DATA button to Confirm","success")
							return appsmith.store.current_Live_card
						}
					storeValue("current_Live_card",appsmith.store.current_Live_card+1)
					return appsmith.store.current_Live_card
					break;
				case 1:
						if(appsmith.store.current_Rnr_card>=appsmith.store.table.length-1)
						{
							GET_DATA.run();
							showAlert("Click Today's RNR button to Confirm","success")
							return appsmith.store.current_Rnr_card
						}
					storeValue("current_Rnr_card",appsmith.store.current_Rnr_card+1)
					return appsmith.store.current_Rnr_card
					break;
				case 2:
					if(appsmith.store.current_Callback_card>=appsmith.store.card.length-1)
						{
							GET_DATA.run();
							showAlert("Click Callbacks button to Confirm","success")
							return appsmith.store.current_Callback_card
						}
					storeValue("current_Callback_card",appsmith.store.current_Callback_card+1)
					return appsmith.store.current_Callback_card
					break;
				case 3:
					if(appsmith.store.current_Followups_card>=appsmith.store.card.length-1)
						{
							GET_DATA.run();
							showAlert("Click Followups button to confirm","success")
							return appsmith.store.current_Followups_card
						}
					storeValue("current_Followups_card",appsmith.store.current_Followups_card+1)
					return appsmith.store.current_Followups_card
					break;
				case 4:
					showAlert("Data updated","success")
					break
				default: 
					return Error()
			}

	},
	pegereload:()=>{
		storeValue("current_Live_card",0);
		storeValue("table",SELECT_DATA.get_Live_data()());
		storeValue("current_demo_card",0);
		storeValue("table",SELECT_DATA.get_request_data())
		storeValue("current_Callback_card",0);
		storeValue("table",SELECT_DATA.get_Callback_data())
		storeValue("current_Followups_card",0);
		storeValue("table",SELECT_DATA.get_Followups_data())
	},



	myFun2: async () => {
		//use async-await or promises
	}
}
